//https://developer.mozilla.org/en-US/docs/Web/Guide/API/DOM/Using_full_screen_mode
// does not work for iOS 
/*jslint browser:true */
function toggleFullScreen() {
  if (!document.fullscreenElement &&    // alternative standard method
      !document.mozFullScreenElement && !document.webkitFullscreenElement && !document.msFullscreenElement ) {  // current working methods
    if (document.documentElement.requestFullscreen) {
      document.documentElement.requestFullscreen();
    } else if (document.documentElement.msRequestFullscreen) {
      document.documentElement.msRequestFullscreen();
    } else if (document.documentElement.mozRequestFullScreen) {
      document.documentElement.mozRequestFullScreen();
    } else if (document.documentElement.webkitRequestFullscreen) {
      document.documentElement.webkitRequestFullscreen();
    }
  } else {
    if (document.exitFullscreen) {
      document.exitFullscreen();
    } else if (document.msExitFullscreen) {
      document.msExitFullscreen();
    } else if (document.mozCancelFullScreen) {
      document.mozCancelFullScreen();
    } else if (document.webkitExitFullscreen) {
      document.webkitExitFullscreen();
    }
  }
}

// computes intensity in response to mouse action
function computeIntensity () {
  var x=_view.DrawingPanel.getMouseX();
  var sc=x/Math.sqrt(x*x+L*L)*xscale;
  var alpha=pi*a*sc/lambda;
  var beta=pi*d*sc/lambda;
  if(alpha===0) {
    mouseReading=Imax*yscale;
    return;
  }
  var cst=Math.sin(alpha)/alpha;
  if(!singleSlit){
    cst=cst*Math.sin(N*beta)/Math.sin(beta)/N;
  }
  mouseReading=cst*cst*yscale;
}

// computes intensity in response to mouse action
// added by lookang to adapt to right panel
function computeIntensityy () {
  var x=_view.right.getMouseY();
  var sc=x/Math.sqrt(x*x+L*L)*xscale;
  var alpha=pi*a*sc/lambda;
  var beta=pi*d*sc/lambda;
  if(alpha===0) {
    mouseReadingy=Imax*yscale;
    return;
  }
  var cst=Math.sin(alpha)/alpha;
  if(!singleSlit){
    cst=cst*Math.sin(N*beta)/Math.sin(beta)/N;
  }
  mouseReadingy=cst*cst*yscale;
}

function compute_y () {
var y=_view.right.getMouseY()/200;
yvalue = y; 
yvalueslider = yvalue*100;
ystring = " y = "+ _view._format(y,"0.000")+ " m ";
return ;
}

function draw_interference () {
for (var i=0; i<nx; i++) {
    x = 0.01+(200-0.01)*i/nx;
    c = pi/(lambdar*x)*xscale/2.6; // factor 2.6 determined by trial and error lookang

    for (var j=0; j<ny; j++) {
      y = 100+(200)*j/ny;
      if (N==1)  {
        sum2[i][j]= 2.0*Math.abs((Math.sin(c*ar*(y-200.0)))/(c*ar*(y-200.0)));
                 }
      else {
        if (N==2) {
        //  sum2[i][j]= 2.0*Math.cos(c*dr*(y-200.0))*Math.cos(c*dr*(y-200.0));
        //          }
       // else {
          sum2[i][j]= 2.0*Math.cos(c*dr*(y-200.0))*Math.cos(c*dr*(y-200.0))*Math.abs((Math.sin(c*ar*(y-200.0)))/(c*ar*(y-200.0)));
             }
           }
    if (y == 200) { // centre line draw max
        sum2[i][j]= 2.0;
                    }
     if (x > L) { // outside screen draw zero
        sum2[i][j]= 0.0;
                 }
}
}
