Content.Code:
//set parameters
step=(range-xmin)/1000;
h=step*7;//the factor is a compromise between resolution and rest error
s=1.0/2.0/h;
x0=(range+xmin+0.0000001)/2.0;//avoids division by 0

//set back check boxes
_0=true;
_1=true;
_2=false;
_3=false;
_4=false;
_5=false;
_6=false;
_7=false;
_8=false;
_9=false;
//calculate
derivative() ;

// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

//defines selected function

if (B==true){
A=1;
range=3*Math.PI;
xmin=0;
ymax=1.5;
ymin=-1.5;}
if (C==true){
A=2;
range=2*Math.PI;
xmin=0;
ymax=1.5;
ymin=-1.5;}
if (D==true){
A=3;
range=1.2;
xmin=0;
ymax=5;
ymin=-1.0;}
if (E==true){
A=4;
range=3.0;
xmin=-3;
ymax=1.3;
ymin=-0.1;}
if (F==true){
A=5;
range=13.0;
xmin=-13.0;
ymax=1.2;
ymin=-0.3;}
if (G==true){
A=6;
range=13.0;
xmin=-13;
ymax=1.2;
ymin=-0.3;}
if (H==true){
A=7;
range=3*Math.PI;
xmin=-1.5;
ymax=1.5;
ymin=-1.5;}
if (I==true){
A=8;
range=2.0;
xmin=-2;
ymax=8;
ymin=-0.5;}

public void derivative () {
  derivative_x0 () ;
for (int j=0; j<m ; j++) {
x[j]=xmin+step*j;
for (int i=0; i<n ; i++) {
//defines selected base function
//calculates base function and y value right and left to x[j]
if (A==1) {
y[j]=Math.sin(x[j]);  
y2[i][j]=Math.sin(x[j]+h*i);
y1[i][j]=Math.sin(x[j]-h*i);
}
 if (A==2) {
y[j]=Math.sin(x[j]*x[j]);
y2[i][j]=Math.sin((x[j]+h*i)*(x[j]+h*i));
y1[i][j]=Math.sin((x[j]-h*i)*(x[j]-h*i));
}
 if (A==3) {
y[j]=Math.pow(x[j],9);
y2[i][j]=Math.pow((x[j]+h*i),9);
y1[i][j]=Math.pow((x[j]-h*i),9);
}
 if (A==4) {
y[j]=Math.exp(-x[j]*x[j]);
y2[i][j]=Math.exp(-(x[j]+h*i)*(x[j]+h*i));
y1[i][j]=Math.exp(-(x[j]-h*i)*(x[j]-h*i));
}
 if (A==5) {
y[j]=Math.sin(x[j])/x[j];
y2[i][j]=Math.sin(x[j]+h*i)/(x[j]+h*i);
y1[i][j]=Math.sin(x[j]-h*i)/(x[j]-h*i);
}
 if (A==6) {
y[j]=Math.sin(x[j])*Math.sin(x[j])/((x[j])*(x[j]));
y2[i][j]=Math.sin(x[j]+h*i)*Math.sin(x[j]+h*i)/((x[j]+h*i)*(x[j]+h*i));
y1[i][j]=Math.sin(x[j]-h*i)*Math.sin(x[j]-h*i)/((x[j]-h*i)*(x[j]-h*i));
}
 if (A==7) {
y[j]=Math.sin(x[j])*Math.sin(x[j]);
y2[i][j]=Math.sin(x[j]+h*i)*Math.sin(x[j]+h*i);
y1[i][j]=Math.sin(x[j]-h*i)*Math.sin(x[j]-h*i);
}
if (A==8) {
y[j]=Math.exp(x[j]);
y2[i][j]=Math.exp(x[j]+h*i);
y1[i][j]=Math.exp(x[j]-h*i);
}
//calculates derivatives as derivative of derivative
//using only values of the base function itself.
a1[j]=Math.pow(s,1)*(y2[1][j]-y1[1][j]);
a2[j]=Math.pow(s,2)*(y2[2][j]-2*y2[0][j]+y1[2][j]);
a3[j]=Math.pow(s,3)*(y2[3][j]-3*y2[1][j]+3*y1[1][j]-y1[3][j]);
a4[j]=Math.pow(s,4)*(y2[4][j]-4*y2[2][j]+6*y2[0][j]-4*y1[2][j]+y1[4][j]);
a5[j]=Math.pow(s,5)*(y2[5][j]-5*y2[3][j]+10*y2[1][j]-10*y1[1][j]+5*y1[3][j]-y1[5][j]);
a6[j]=Math.pow(s,6)*(y2[6][j]-6*y2[4][j]+15*y2[2][j]-20*y2[0][j]+15*y1[2][j]-6*y1[4][j]+y1[6][j]);
a7[j]=Math.pow(s,7)*(y2[7][j]-7*y2[5][j]+21*y2[3][j]-35*y2[1][j]+35*y1[1][j]-21*y1[3][j]+7*y1[5][j]-y1[7][j]);
a8[j]=Math.pow(s,8)*(y2[8][j]-8*y2[6][j]+28*y2[4][j]-56*y2[2][j]+70*y2[0][j]-56*y1[2][j]+28*y1[4][j]-8*y1[6][j]+y1[8][j]);
a9[j]=Math.pow(s,9)*(y2[9][j]-9*y2[7][j]+36*y2[5][j]-84*y2[3][j]+126*y2[1][j]-126*y1[1][j]+84*y1[3][j]-36*y1[5][j]+9*y1[7][j]-y1[9][j]);
}
}
}

public void derivative_x0 () {
for (int i=0; i<n ; i++) {
//defines selected base function
//calculates base function and y value right and left to x0
if (A==1) {
y0=Math.sin(x0);  
y20[i]=Math.sin(x0+h*i);
y10[i]=Math.sin(x0-h*i);
}
 if (A==2) {
y0=Math.sin(x0*x0);
y20[i]=Math.sin((x0+h*i)*(x0+h*i));
y10[i]=Math.sin((x0-h*i)*(x0 -h*i));
}
 if (A==3) {
y0=Math.pow(x0,9);
y20[i]=Math.pow((x0+h*i),9);
y10[i]=Math.pow((x0-h*i),9);
}
 if (A==4) {
y0=Math.exp(-x0*x0);
y20[i]=Math.exp(-(x0+h*i)*(x0+h*i));
y10[i]=Math.exp(-(x0-h*i)*(x0-h*i));
}
 if (A==5) {
y0=Math.sin(x0)/x0;
y20[i]=Math.sin(x0+h*i)/(x0+h*i);
y10[i]=Math.sin(x0-h*i)/(x0-h*i);
}
 if (A==6) {
y0=Math.sin(x0)*Math.sin(x0)/((x0)*(x0));
y20[i]=Math.sin(x0+h*i)*Math.sin(x0+h*i)/((x0+h*i)*(x0+h*i));
y10[i]=Math.sin(x0-h*i)*Math.sin(x0-h*i)/((x0-h*i)*(x0-h*i));
}
if (A==7) {
y0=Math.sin(x0)*Math.sin(x0);
y20[i]=Math.sin(x0+h*i)*Math.sin(x0+h*i);
y10[i]=Math.sin(x0-h*i)*Math.sin(x0-h*i);
}
if (A==8) {
y0=Math.exp(x0);
y20[i]=Math.exp(x0+h*i);
y10[i]=Math.exp(x0-h*i);
}

//calculates derivatives as derivative of derivative
//using only values of the base function itself.
a10=Math.pow(s,1)*(y20[1]-y10[1]);
a20=Math.pow(s,2)*(y20[2]-2*y20[0]+y10[2]);
a30=Math.pow(s,3)*(y20[3]-3*y20[1]+3*y10[1]-y10[3]);
a40=Math.pow(s,4)*(y20[4]-4*y20[2]+6*y20[0]-4*y10[2]+y10[4]);
a50=Math.pow(s,5)*(y20[5]-5*y20[3]+10*y20[1]-10*y10[1]+5*y10[3]-y10[5]);
a60=Math.pow(s,6)*(y20[6]-6*y20[4]+15*y20[2]-20*y20[0]+15*y10[2]-6*y10[4]+y10[6]);
a70=Math.pow(s,7)*(y20[7]-7*y20[5]+21*y20[3]-35*y20[1]+35*y10[1]-21*y10[3]+7*y10[5]-y10[7]);
a80=Math.pow(s,8)*(y20[8]-8*y20[6]+28*y20[4]-56*y20[2]+70*y20[0]-56*y10[2]+28*y10[4]-8*y10[6]+y10[8]);
a90=Math.pow(s,9)*(y20[9]-9*y20[7]+36*y20[5]-84*y20[3]+126*y20[1]-126*y10[1]+84*y10[3]-36*y10[5]+9*y10[7]-y10  [9]);
}
}
