// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

x1=L1*Math.sin(a1);
y1=-L1*Math.cos(a1);
x2=L2*Math.sin(a2);
y2=-L2*Math.cos(a2);

if(L1/L2>0.0 && m2>0.0){   //excludes singularity for L2=0 and artifacts at m = 0
y1ZweiStrich = 1/(m1+m2*Math.sin(a1-a2)*Math.sin(a1-a2))*
(-g/L1*((m1+m2)*Math.sin(a1)- m2*Math.sin(a2)*Math.cos(a1-a2))
-m2*Math.sin(a1-a2)*(L2/L1*va2*va2+va1*va1*Math.cos(a1-a2)));
}
else{
y1ZweiStrich =g/L1*Math.sin(a1); 
}

if(L1/L2>0.0 && m2>0.0){   //excludes singularity for L2=0 and artifacts at m = 0
y2ZweiStrich = -g/L2*Math.sin(a2)+L1/L2*va1*va1*Math.sin(a1-a2)
-Math.cos(a1-a2)*L1/L2*1/(m1+m2*Math.sin(a1-a2)*Math.sin(a1-a2))*
(-g/L1*((m1+m2)*Math.sin(a1)- m2*Math.sin(a2)*Math.cos(a1-a2))
-m2*Math.sin(a1-a2)*(L2/L1*va2*va2+va1*va1*Math.cos(a1-a2)));
}
else{
y1ZweiStrich =0.0; 
}

public double getB1 (double a1, double a2, double va1,double va2) {
if ( L2/L1 > 0.0 && m2>0.0 ) {   //excludes singularity and noise
return  1/(m1+m2*Math.sin(a1-a2)*Math.sin(a1-a2))*
(-g/L1*((m1+m2)*Math.sin(a1)- m2*Math.sin(a2)*Math.cos(a1-a2))
-m2*Math.sin(a1-a2)*(L2/L1*va2*va2+va1*va1*Math.cos(a1-a2)));
}
else
return -g/L1*Math.sin(a1);//equation of the simple Pendulum
}
public double getB2 (double a1,double a2,double va1,double va2) {
if ( L2/L1 > 0.0 && m2>0.0 ) {   //excludes singularity and noise
return -g/L2*Math.sin(a2)+L1/L2*va1*va1*Math.sin(a1-a2)
-Math.cos(a1-a2)*L1/L2*1/(m1+m2*Math.sin(a1-a2)*Math.sin(a1-a2))*
(-g/L1*((m1+m2)*Math.sin(a1)- m2*Math.sin(a2)*Math.cos(a1-a2))
-m2*Math.sin(a1-a2)*(L2/L1*va2*va2+va1*va1*Math.cos(a1-a2)));
}
else 
return 0; //Zero for the single pendulum
}

public void clear(){
_resetView();
_initialize();
}

public void Pendel () {
a1=Math.atan2(x1,-y1);
//a2=Math.atan2(x2,-y2);//leaves otientation of P2
a2=a1;//straightens the pendula.
va1=0.0;
va2=0.0;
}

public void reset_pause () {
a1=Math.PI/2;  
a2=a1;//straightens the pendula.
va1=0.0;
va2=0.0;
_pause();
}
