_view.clearData();

// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

public void Listen() {

 //List of Function type
  descriptionList = new java.util.ArrayList();
  // List of Formula
  y_functionList = new java.util.ArrayList();
  //List of x range
  xmaxList = new java.util.ArrayList();
  //Fill List 
        y_functionList.add("y");
	descriptionList.add("Exponential e ^ x");	         
        xmaxList.add(new Double (3));

	y_functionList.add("-y");
	descriptionList.add("Exponential_damping e ^ (- x)");	
        xmaxList.add(new Double(3));

      	y_functionList.add("y*sin(x)");
	descriptionList.add("Periodic");	
        xmaxList.add(new Double(20));  

        y_functionList.add("y*(x/5)*sin(x)");
	descriptionList.add("Oscillation_divergent");
        xmaxList.add(new Double(20)); 

       	y_functionList.add("(y/x)*sin(x)");
	descriptionList.add("Oscillation_convergent");	
        xmaxList.add(new Double(20));

        y_functionList.add("y*sin((x-10)^2)");
	descriptionList.add("Oscillation_quadratic");	
        xmaxList.add(new Double(20));

        y_functionList.add("y^sin(x)");
	descriptionList.add("Sine_exponent");	
        xmaxList.add(new Double(20));

        y_functionList.add("1");
	descriptionList.add("Constant_velocity");	
        xmaxList.add(new Double(10)); 

        y_functionList.add("x");
	descriptionList.add("Constant_acceleration");	
        xmaxList.add(new Double(10));      
}

public void initFunctions(){
  String fu  = (String)descriptionList.get(0);
  for (int i = 1; i < descriptionList.size(); i++ ) {
  String d = ((String)descriptionList.get(i)).trim();
      fu = fu + "; " + d;
}
  //String transcribed in  "descriptions" 
  description = fu;  
  //first function:
  y_functions = y_functionList.get(0);
  xmax=xmaxList.get(0);
  }

public void changeFunction() {
// where in the list is the chosen function??
if (descriptionList!=null) {
int pos = descriptionList.indexOf(fTyp.trim());
//get the function in Field Fx_2
y_functions = y_functionList.get(pos);
//get x range
xmax=xmaxList.get(pos);
restart () ;
}
}

public void resetObject () {
 _pause();
 x=0.0;
 y=Anfangswert_y;
 x=xo;
}

public void loeschen() {
resetObject ();  
_view.resetTraces();
}

public void restart () {
xo=0;
x=xo;
dx=0.05;
Anfangswert_y = 1;
y = Anfangswert_y;
max=1.1;
_view.clearData();
}
