// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

public void Listen() {

 //Lis of function type
  descriptionList = new java.util.ArrayList();
  // List �f formulas
  y_functionList = new java.util.ArrayList();
  //List of range of coordinatess
  xmaxList = new java.util.ArrayList();
  //List of initial y
  yAnfangList=new java.util.ArrayList();
  //List of initial y�
  yStrichAnfangList=new java.util.ArrayList();
  //fill lists
        y_functionList.add("-y");
	descriptionList.add("Cosine");	         
        xmaxList.add(new Double (4*Math.PI));
        yAnfangList.add(new Double(1));
        yStrichAnfangList.add(new Double(0));

	y_functionList.add("-y");
	descriptionList.add("Sine");	
        xmaxList.add(new Double(4*Math.PI));
        yAnfangList.add(new Double(0));
        yStrichAnfangList.add(new Double(1));         

      	y_functionList.add("y");
	descriptionList.add("Exponential");	
        xmaxList.add(new Double(3));  
        yAnfangList.add(new Double(1));
        yStrichAnfangList.add(new Double(1));        

        y_functionList.add("y");
	descriptionList.add("Exponential_damping");	
        xmaxList.add(new Double(3));  
        yAnfangList.add(new Double(1));
        yStrichAnfangList.add(new Double(-1));        

        y_functionList.add("y");
	descriptionList.add("Hyperbolic_sine");
        xmaxList.add(new Double(3)); 
        yAnfangList.add(new Double(0));
        yStrichAnfangList.add(new Double(1));        

       	y_functionList.add("y");
	descriptionList.add("Hyperbolic_cosine");	
        xmaxList.add(new Double(3));
        yAnfangList.add(new Double (1));
        yStrichAnfangList.add(new Double (0));        

        y_functionList.add("-5*y/(1+x)");
	descriptionList.add("Slowing_oscillation");	
        xmaxList.add(new Double(20));
        yAnfangList.add(new Double(0));
        yStrichAnfangList.add(new Double(1));   

        y_functionList.add("-y*x");
	descriptionList.add("Speeding_oscillation");	
        xmaxList.add(new Double(20));
        yAnfangList.add(new Double(0));
        yStrichAnfangList.add(new Double(1)); 

        y_functionList.add("-y - 0.2*yStrich");
	descriptionList.add("Damped_oscillation");	
        xmaxList.add(new Double(20));
        yAnfangList.add(new Double(0));
        yStrichAnfangList.add(new Double(1));   

        y_functionList.add("-y + 0.2*yStrich");
	descriptionList.add("Increasing_oscillation");	
        xmaxList.add(new Double(20));
        yAnfangList.add(new Double(0));
        yStrichAnfangList.add(new Double(1));        
}

public void initFunctions(){
  String fu  = (String)descriptionList.get(0);
  for (int i = 1; i < descriptionList.size(); i++ ) {
  String d = ((String)descriptionList.get(i)).trim();
      fu = fu + "; " + d;
}
  //String is transcribed in "descriptions" 
  description = fu;  
  //first function 
  y_functions = y_functionList.get(0);
  xmax=xmaxList.get(0);
  Anfangswert_y=yAnfangList.get(0);
  Anfangswert_yStrich=yStrichAnfangList.get(0);
  resetObject () ;
  }

public void changeFunction() {
// position of chosen function
if (descriptionList!=null) {
int pos = descriptionList.indexOf(fTyp.trim());
//get function from Field Fx_2
y_functions = y_functionList.get(pos);
//get range of variables
xmax=xmaxList.get(pos);
Anfangswert_y=yAnfangList.get(pos);
Anfangswert_yStrich=yStrichAnfangList.get(pos);
resetObject () ;;
}
}

public void resetObject () {
 _pause();
 x=0.0;
 y=Anfangswert_y;
 yStrich=Anfangswert_yStrich;
 x=xo;
}

public void loeschen() {
resetObject ();  
_view.resetTraces();
}

public void restart () {
xo=0;
x=xo;
dx=0.05;
Anfangswert_y = 1;
Anfangswert_yStrich = 0;
y = Anfangswert_y;
yStrich = Anfangswert_yStrich;
max=1.1;
xmax=3;
_view.clearData();
}
