if(qm_system.trim().equals("ISW")) {
  first=1;
  ISW_Superpostion();
} else {
  first=0;
  SHO_Superpostion();
}

public void ISW_Superpostion () {
  double x0=-a, dx=2*a/(x.length-1);
  double k0=Math.PI/a;
  double E0=k0*k0/2;
  double norm=Math.sqrt(0.5/a);
  for(int i=0, n=x.length; i<n; i++) {
    x[i]=x0;
    rePsi[i]=0;
    imPsi[i]=0;
    for(int s=1, ns=outArray.length; s<=ns;s++) {
      if(Math.abs(x0)>=a/2)continue;  //psi is zero outside of box
      double psiRe=0, psiIm=0;
      if(s%2==0){
        psiRe=norm*outArray[s-1][1]*Math.sin(s*k0*x0);
        psiIm=norm*outArray[s-1][2]*Math.sin(s*k0*x0);
      }else{
        psiRe=norm*outArray[s-1][1]*Math.cos(s*k0*x0);
        psiIm=norm*outArray[s-1][2]*Math.cos(s*k0*x0);
      }
      rePsi[i]+=psiRe*Math.cos(-s*s*E0*t)-psiIm*Math.sin(-s*s*E0*t);
      imPsi[i]+=psiRe*Math.sin(-s*s*E0*t)+psiIm*Math.cos(-s*s*E0*t);
    }
    x0+=dx;
  }
}

public void SHO_Superpostion () {
  double x0=-2.5*a, dx=5*a/(x.length-1);
  double norm=Math.pow(Math.PI,0.25)/Math.sqrt(a);
  double scale=Math.PI/a;
  for(int i=0, n=x.length; i<n; i++) {
    x[i]=x0;
    double xi=x0*scale;
    rePsi[i]=0;
    imPsi[i]=0;
    for(int s=0, ns=outArray.length; s<ns;s++) {
      double E=(s+0.5)*Math.PI*Math.PI/a/a;
      if(H[s]==null)break;
      double psiRe=norm*outArray[s][1]*Math.exp(-xi*xi/2)*((Function)H[s]).evaluate(xi);
      double psiIm=norm*outArray[s][2]*Math.exp(-xi*xi/2)*((Function)H[s]).evaluate(xi);
      rePsi[i]+=psiRe*Math.cos(-E*t)-psiIm*Math.sin(-E*t);
      imPsi[i]+=psiRe*Math.sin(-E*t)+psiIm*Math.cos(-E*t);
    }
    x0+=dx;
  }
}

public void normalize() {
  double sum=0;
  for(int s=0, ns=outArray.length; s<ns;s++) {
      sum += outArray[s][1]*outArray[s][1]+outArray[s][2]*outArray[s][2];
  }
  sum=(sum==0)?1:Math.sqrt(sum);  // trap for all zero coefficients
  for(int s=0, ns=outArray.length; s<ns;s++) {
      outArray[s][1] /= sum;
      outArray[s][2] /= sum;
  }
  _view.coefficientArray.refreshTable();  //static tables must be refreshed explicilty
}

public void compute_Eigenvalues () {
  t=0;
  for(int s=0, ns=outArray.length; s<ns;s++) {
    if(qm_system.equals("ISW")) {
      outArray[s][0]=iswEnergy(s);
    } else {
      outArray[s][0]=shoEnergy(s);
    }
  }
}

double iswEnergy(int n) {
  n++;
  double k0=n*Math.PI/a;
  return k0*k0/2;
}

double shoEnergy(int n) {
  return (n+0.5)*Math.PI*Math.PI/a/a;
}

private void readDataFromFile () {
  String filename="SuperpositionData.txt";
  String contents = org.opensourcephysics.tools.ResourceLoader.getString(filename);
  if (contents==null) {
    System.out.println ("Error reading "+filename);
    return;
  }
  scenarioData = contents.split("#");
  options="";
  scenarioNames = new String[scenarioData.length];
  for(int i=0, n=scenarioData.length; i<n; i++){
    String[] lines = scenarioData[i].split("\n");
    scenarioNames[i]="";
    if(lines==null||lines.length<10) continue;
    scenarioNames[i]=lines[0].trim();
    options+=lines[0].trim()+';';
  }
  selection=scenarioNames[1];
  readScenario(scenarioData[1]);  // default scenario
}

public void selectScenario() {
  _pause();
  for(int i=0, n=scenarioData.length; i<n; i++) {
    if(selection.equals(scenarioNames[i])) {
      readScenario(scenarioData[i]);
      break;
    }
  }
}

private void readScenario(String scenario) {
  String[] lines = scenario.split("\n");
  if(lines==null||lines.length<10) return;
  //read QM system
  String line = lines[1].trim();
  int pos = line.indexOf("=");
  qm_system=line.substring(pos + 1).trim();
  // read centered
  line = lines[2].trim();
  centered= (line.substring(pos + 1).trim()).equals("true");
  // read display mode
  line = lines[3].trim();
  pos = line.indexOf("=");
  displayMode = (int)Double.parseDouble(line.substring(pos + 1).trim());
  // read number of points in wave function
  line = lines[4].trim();
  pos = line.indexOf("=");
  n= (int)Double.parseDouble(line.substring(pos + 1).trim());
  // read width of potential well
  line = lines[5].trim();
  a = line.indexOf("=");
  a = Double.parseDouble(line.substring(pos + 1).trim());
  //read intial time
  line = lines[6].trim();
  pos = line.indexOf("=");
  t = Double.parseDouble(line.substring(pos + 1).trim());
  // read time step
  line = lines[7].trim();
  pos = line.indexOf("=");
  dt = Double.parseDouble(line.substring(pos + 1).trim());
  // read number of states
  line = lines[8].trim();
  pos = line.indexOf("=");
  states= (int)Double.parseDouble(line.substring(pos + 1).trim());
  allocateArrays();
  for (int i=10,n=lines.length; i<n; i++) {
    String[] pieces = lines[i].trim().split("\\s");
    outArray[i-10][1] = Double.parseDouble(pieces[0]);
    outArray[i-10][2] = Double.parseDouble(pieces[1]);
  }
  compute_Eigenvalues();
 // _view.coefficientArray.refreshTable();
}

public void allocateArrays () {
  x=new double[n];
  rePsi=new double[n];
  imPsi=new double[n];
  outArray = new double[states][3];
  H = new Object[states];
  double PISQRT=Math.sqrt(Math.PI);
  for(int s=0; s<states; s++) {
    // generate Hermite polynomials for each state
    double norm=Math.sqrt(Math.pow(2,s)*Factorials.factorial(s)*PISQRT);
    H[s]= Hermite.getPolynomial(s).divide(norm);
  }
  compute_Eigenvalues () ;
}
