  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

public void Listen() {

 //List of function type
  descriptionList = new java.util.ArrayList();
  // List of formulas
  x_functionList = new java.util.ArrayList();
  y_functionList = new java.util.ArrayList();
  divergenzList=new java.util.ArrayList();
  rotationList=new java.util.ArrayList();

  //fill List

        x_functionList.add("-y^2+y");
	y_functionList.add("x^2+x");
	descriptionList.add("twin vortices (-y^2 + y, x^2 + x)");	
        divergenzList.add("0 + 0 = 0 ");
        rotationList.add("(0,0, 2x+2y) ");

        x_functionList.add("");
	y_functionList.add("");
	descriptionList.add("your data");	
        divergenzList.add("");
        rotationList.add("(  ,  , )");       

        x_functionList.add("1");
	y_functionList.add("0");
	descriptionList.add("constant to the right  (1 , 0)");	
        divergenzList.add("0 + 0 = 0");
        rotationList.add("(0,0,0) = 0");

        x_functionList.add("-1");
	y_functionList.add("0");
	descriptionList.add("constant to the left  (-1 , 0)");	
        divergenzList.add("0 + 0 = 0");
        rotationList.add("(0,0,0) = 0");

        x_functionList.add("1");
	y_functionList.add("1");
	descriptionList.add("constant diagonal  (1 , 1)");	
        divergenzList.add("0 + 0 = 0");
        rotationList.add("(0,0,0) = 0");

        x_functionList.add("x");
	y_functionList.add("y");
	descriptionList.add("radial outward  (x , y)");
        divergenzList.add("? ?");
        rotationList.add("(0,0,0) = 0");

        x_functionList.add("-x");
	y_functionList.add("-y");
	descriptionList.add("radial inward (-x , -y)");
        divergenzList.add("? ?");
        rotationList.add("(0,0,0) = 0");

	x_functionList.add("y");
	y_functionList.add("-x");
	descriptionList.add("vortex right (y , -x)");
        divergenzList.add("0 + 0 = 0");
        rotationList.add("(0,0,-1-1) = (0,0,-2)"); 

        x_functionList.add("-y");
	y_functionList.add("x");
	descriptionList.add("vortex left (-y , x)");
        divergenzList.add("0 + 0 = 0");
        rotationList.add("(0,0,1+1) = (0,0,2)"); 	

        x_functionList.add("0");
	y_functionList.add("-y");
	descriptionList.add("x-line attractive (0 , -y)");
        divergenzList.add("0 - 1 = -1");
        rotationList.add("(0,0,0) = 0"); 	

        x_functionList.add("x");
	y_functionList.add("0");
	descriptionList.add("y_line repuslive (x , 0)");	
        divergenzList.add("1 + 0 = +1");
        rotationList.add("(0,0,0) = 0"); 	

        x_functionList.add("y*(1-x)");
	y_functionList.add("-x*(1-y)");
	descriptionList.add("vortex & source (y (1-x) , -x (1-y))");	
        divergenzList.add("x - y");
        rotationList.add("(0, 0, x + y - 2)"); 

        x_functionList.add("x^2+x");
	y_functionList.add("-y^2+y");
	descriptionList.add("twin source and drain(x^2 + x , -y^2 + y)");	
        divergenzList.add("2x -2y");
        rotationList.add("(0,0,0) = 0"); 

        x_functionList.add("-y^2+y");
	y_functionList.add("x^2+x");
	descriptionList.add("twin vortices (-y^2 + y, x^2 + x)");	
        divergenzList.add("0 + 0 = 0 ");
        rotationList.add("(0,0, 2x+2y) ");
}

public void initFunctions(){
  String fu  = (String)descriptionList.get(0);
  for (int i = 1; i < descriptionList.size(); i++ ) {
  String d = ((String)descriptionList.get(i)).trim();
      fu = fu + "; " + d;
}
  //string transcribed in  "descriptions"
  description = fu;  
  //first function is fetched and calculated:
  x_functions = x_functionList.get(0);
  y_functions = y_functionList.get(0);
  divergenz=divergenzList.get(0);
  rotation=rotationList.get(0);
  }

public void changeFunction() {
// where in the list is the chosen function??
if (descriptionList!=null) {
int pos = descriptionList.indexOf(fTyp.trim());
//get this function from field Fx_2
x_functions = x_functionList.get(pos);
y_functions = y_functionList.get(pos);
divergenz=divergenzList.get(pos);
rotation=rotationList.get(pos);
 }
}

