Content.Code:
_setFPS(speed);    //Zahl der Schritte /Sekunde = speed
x=x0;              //Anfangswert
y0=_view.Funktion.evaluate(x0);
n=1;

// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

//Abbruch bei ueberschreiten des Bereichs
if(x>xmax) { // move when reach right side
  _pause();
}
//Abbruchbedingung; delta = Restabweichung
//einzel: Stop nach Berechnung einer Wurzel
if(  Math.abs(y)<delta) {
  x0=x+delta/10;
  _initialize();
  if (einzel==true) {
    _pause();
  }
}
//Fallunterscheidung von unten, von oben
if (y0<0) {
  dx=0.1/n;
  x=x+dx;
  if (y>0) {
    x=x-2*dx;
    n=10*n;
  }
}
else {
  dx=0.1/n;
  x=x+dx;
  if (y<0) {
    x=x-2*dx;
    n=10*n;
  }
}
