Content.Code:
a=0.3;
b=Math.PI;
c=Math.PI;
max=1;
Listen();
initFunctions();

// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

public void Listen() {

  // List of formulas
  functionList = new java.util.ArrayList();
  //Liste of function type
  descriptionList = new java.util.ArrayList();

	functionList.add("a*sin(b*x-t)");
	descriptionList.add("Plane wave x");

	functionList.add("a*sin(b*y-t)");
	descriptionList.add("Plane wave y");

        functionList.add("0.3*sin(6*pi*a*(b*x+c*y)/sqrt(b*b+c*c)-t)");
	descriptionList.add("Plane wave direction adjustable");

 	functionList.add("a*(sin(b*y-t)+sin(b*y-t))");
 	descriptionList.add("Interference concurrent  f1");

        functionList.add("a*(cos(b*y-t)+cos(-b*y-t))");
	descriptionList.add("Interference countercurrent  f1");

 	functionList.add("a*(sin(b*y-t)+sin(c*y-t))");
	descriptionList.add("Interference concurrent  f1 + f2");

	functionList.add("a*(sin(b*y-t)+sin(-c*y-t))");
	descriptionList.add("Interference countercurrent  f1 + f2");

	functionList.add("a*(sin(b*x-t)+sin(c*y-t))");
	descriptionList.add("Interference perpendicular  f1 + f2");

	functionList.add("a*(sin(b*(y-(c-pi)*x)-t)+sin(b*(y+(c-pi)*x)-t))");
	descriptionList.add("Interference  angle < 90 adjustable (c)");

	functionList.add("a*(sin(b*(y-(c-pi)*x)-t)+sin(b*(-y+(c-pi)*x)-t))");
	descriptionList.add("Interference angle > 90 adjustable (c)");

	functionList.add("a*sin(b*(x*x+y*y)-t)");
	descriptionList.add("Radial wave outward");

	functionList.add("a*sin(b*(x*x+y*y)+t)");
	descriptionList.add("radial wave inward");

        functionList.add("a*(sin(b*(x^2+y^2)-t)+sin(b*(x^2+y^2)+t))");
	descriptionList.add("Standing radial wave");

        functionList.add("0.4*a*sin(b*(x^2+y^2)-t)/sqrt(0.1+x^2+y^2)");
	descriptionList.add("Surface wave outward");

        functionList.add("0.2*a*sin(b*(x^2+y^2)-t)/(0.1+x^2+y^2)");
	descriptionList.add("Spatial wave outward");
}

public void initFunctions() {
  // A string is buit from description list
  // separated by semicolons, fillng the Combobox
  String fu  = (String)descriptionList.get(0);
  for (int i = 1; i < descriptionList.size(); i++ ) {
  String d = ((String)descriptionList.get(i)).trim();
      fu = fu + "; " + d;
}
  //the string is transcribed into "descriptions" 
  description = fu;  
  //the first function is fetched and calculated
    functions = functionList.get(0);
    }

public void changeFunction() {
// with change of function parameters are reset
a=0.3;
b=Math.PI;
c=Math.PI;
p=1.0;
t=0.0;
// where in the list is the selscted function??
if (descriptionList!=null) {
int pos = descriptionList.indexOf(fTyp.trim());
//^fetch function to Field Fx_2
functions = functionList.get(pos);
 }
}

public void ParamReset () {
a=0.3;
b=Math.PI;
c=Math.PI;
p=1.0;
t=0.0;
}
