// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

// fetch integral value
if(k<(q-1)) {
  dy[k+1] = yt;
}
if(k<q) {
	k++;
}
else {
 _pause();
}

public void initFunctions() {
	// List of formula
	functionList = new java.util.ArrayList();
	//List of function typw
	descriptionList = new java.util.ArrayList();
	// List of start values m
        minList = new java.util.ArrayList();
        // List of end values 
	maxList = new java.util.ArrayList();
        // List of initial values 
	startwertList = new java.util.ArrayList();

        // fill Lists
	functionList.add("          choose a function! ");
	descriptionList.add(" ");
	minList.add(new Double(0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(-1));

	functionList.add("a * (x - pi + b ) * cos(n*x)");
	descriptionList.add("Sawtooth cos");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * (x - pi + b) *  sin(n*x)");
	descriptionList.add("Sawtooth sin");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * step(x - pi + b) *  cos(n*x)");
	descriptionList.add("Rectangle cos");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * step(x - pi + b) *  sin(n*x)");
	descriptionList.add("Rectangle sin");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * (step(x - pi + b)- step(x  -pi + c/4)) *  cos(n*x)");
	descriptionList.add("Rectangle Pulse cos");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * (step(x - pi + b)- step(x  -pi + c/4)) *  sin(n*x)");
	descriptionList.add("Rectangle Pulse sin");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * e^(-c^2*(x - pi + b)^2) *  cos(n*x)");
	descriptionList.add("Gaussian Pulse cos");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));

	functionList.add("a * e^(-c^2*(x - pi+ b)^2) *  sin(n*x)");
	descriptionList.add("Gaussian Pulse sin");
	minList.add(new Double(0.0));
	maxList.add(new Double(2*Math.PI));
	startwertList.add(new Double(0.0));


	// a formula string is produced, separated by colons
	// and filled into Combobox 
	String fu  = (String)functionList.get(0);
	for (int i = 1; i < functionList.size(); i++ ){
		String d = ((String)functionList.get(i)).trim();
		fu = fu + "; " + d;
	}
        functions = fu;
        description = descriptionList.get(0);
        // get function
        changeFunction();
      }

public void changeFunction() {
	// initialize description
	if(functionList != null) {
		// where is the selected function in the list ?
		int pos = functionList.indexOf(Fx.trim());
		if(pos >= 0) {
			// get description
			description = descriptionList.get(pos);
			// set start value
			Double mi = minList.get(pos);
      	                if(mi != null) {
      	                   min = mi.doubleValue(); 
			}
                        // set end vlue
                        Double ma = maxList.get(pos);
      	                if(ma != null) {
      	                   max = ma.doubleValue(); 
			}
                        // set initial value
			Double sw = startwertList.get(pos);
      	                if(sw != null) {
      	                    ya0 = sw.doubleValue(); 
			}
		}
	}
	else {
		System.out.println("leere functionList");
	}
        /* get function and derivative */
        f = _view.getFunction("FieldFx2","function");
	double h = (Math.abs(min) + Math.abs(max)) / (q*5); // Schrittweite fuer die Naeherung
	// get parameters and calculate
        a=1;
        b=0;
        c=1;
       n=10;
        calculate();
}

public void calculate() {
  // set step width
  step = (max - min) / (q-1);
  // set x and calculate function
  for (int i = 0; i<q; i++) {
	x[i] = min + step * i;
	y[i]= f.eval(x[i]);
  }
  // initialize evolution
  t=min;
  k=0;
  yt=ya0;
  dy[0] = ya0;

  // start evolution of integral and derivative 
  _play();
}
