Content.Code:
// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

public void _reset () {
  k = 0;
  m = 0;
  origin ();
  default_Julia ();
  close=0.2;
  }

public void computeSet () {
  for (int i=0; i<n; i++) {
    double im = imMin + (imMax - imMin) * i / (double) (n-1);
    for (int ix = 0; ix < n; ix++) {
      double re = reMin + (reMax - reMin) * ix / (double) (n-1);
      data[ix][i] = -128+ iteration(re, im);
    }
  } 
}

public int iteration (double re, double im) {
  // algorithm  Take z, square it, add the number passed as a parameter.
  int count = Byte.MIN_VALUE;
  double a = 0, b = 0, temp1 = 0;
  while (count < Byte.MAX_VALUE) {
   temp1 = a;//temp eingefuehrt, damit in b nicht der bereits geaenderte Wert benutzt wird//
    a = (a )* (a ) - b * b + re;
    b = 2 * temp1 * b + im;
    if (a * a + b * b > 4.0) break;
    count++;
  }
  return 10 * count;
}

public void default_Julia () {
  origin_Julia ();
  }

public void computeJulia () {
  for (int i=0; i<n; i++) {
    double im = imMinJ + (imMaxJ - imMinJ) * i / (double) (n-1) - 0.119;
    for (int ix = 0; ix < n; ix++) {
      double re = reMinJ + (reMaxJ - reMinJ) * ix / (double) (n-1);
      dataJulia[ix][i] = -128 + iterationJulia(re, im)
      ;
    }
  } 
}

public int iterationJulia (double re, double im) {
  // algorithm  Take c, square it, add the number passed as z.
  int count = Byte.MIN_VALUE;
  double a = k, b = m, temp1 = k;
  while (count < Byte.MAX_VALUE) {
    temp1 = re;
    re = (re )* (re ) - im * im + a;
    im = 2 * temp1 * im + b;
    if (re * re + im * im > 4.0) break;//Abbruch auï¿½en
    if (re * re + im * im < close) break;//Abruch innen
    count++;
  }
  return 10 * count;
}

public void mouseDragged () {
  if (firstPointTaken) { dx = x - re1; dy = im1 - y; }
  else { re1 = x; im1 = y; dx = dy = 0; firstPointTaken = true; }
}
public void mousePressed () {
  firstPointTaken = false;
}
public void mouseReleased () {
  firstPointTaken = false;
  if (dx>0 && dy>0) {
    reMin = re1; reMax = re1 + dx;
    imMin = im1-dy; imMax = im1;
    computeSet();
  }
}

public void origin () {
 firstPointTaken = false;
  if (dx>0 && dy>0) {
    reMin = -2; reMax = 1.0 ;
    imMin = -1.5; imMax = 1.5;
    computeSet();
  }
}

public void mouseDragged_Julia () {
  if (firstPointTaken) { dx = x - re1; dy = im1 - y; }
  else { re1 = x; im1 = y; dx = dy = 0; firstPointTaken = true; }
}

public void mousePressed_Julia () {
  firstPointTaken = false;
}
public void mouseReleased_Julia () {
  firstPointTaken = false;
  if (dx>0 && dy>0) {
    reMinJ = re1; reMaxJ = re1 + dx;
    imMinJ = im1-dy; imMaxJ = im1;
    computeJulia();
  }
}

public void origin_Julia () {
    reMinJ = -2; reMaxJ = 2;
    imMinJ = -2; imMaxJ = 2;
    computeJulia();
  }
