Content.Code:
//scales are calculated by an approximating formula
rmin=0.7887*Math.pow(k,-0.7912);
rmax=1.0447*Math.pow(k,-0.8022);
r = 0.0;

// for the html- Link version this start the description window 
//together with the simulation windows
Thread thread = new Thread(new Runnable() {
  public void run() {
    try { Thread.sleep(500); }
    catch (Exception exc) {}
    _view.showDescription(true);
  }
});
thread.start();

double y = Math.random();
for (int i=0; i<points; i++) y = 4*r*y*(1.0-Math.pow(y,k));  // Ignore the first iterations
orbit[0] = y;
for (int i=1; i<points; i++) orbit[i] = 4*r*orbit[i-1]*(1.0-Math.pow((orbit[i-1]),k));

_view.Datenraster_1.setColor(0,java.awt.Color.BLUE);
_view.Datenraster_2.setColor(1,java.awt.Color.RED);
