//"XYZ;XZY;YXZ;YZX;ZXY;ZYX"

if (mode.equals("XYZ")){
  modemap = 0;
}
else if (mode.equals("XZY"))
{
  modemap = 1;
}
else if (mode.equals("YXZ"))modemap = 2;
else if (mode.equals("YZX"))modemap = 3;
else if (mode.equals("ZXY"))modemap = 4;
else if (mode.equals("ZYX"))modemap = 5;
else {
  modemap = 0;
}

varinit();
getID();
for(int i=0;i<n;i++){
 x[i]=0.;
 y[i]=ymin+dy*i;
 z[i]=0.;
}

for(int i=0;i<n;i++){
 Ez[i]=E0*Math.sin(k*(y[i]-Vy*t));
 Bx[i]=B0*Math.sin(k*(y[i]-Vy*t));
 vEz[i] = Ez[i]*1.5; // added by lookang for drawing in new 3D panel
 vBx[i]= Bx[i]*20; // added by lookang for drawing in new 3D panel
}
getID();

public void varinit () {
 omega=PI2/period;
 k=omega/Vy;
 wavelength=PI2/k;
 B0=E0/Vy;
 t=0.;
_pause();
}
public void getID(){
 id=(int)(yp/dy);
 yp=id*dy;
 E=Ez[id]*1.5;
 Es=Ez[0]*1.5;
 B=Bx[id]*20;
}
