public void varinit () {
 omega=PI2/period;
 k=omega/Vy;
 wavelength=PI2/k;
 B1=E1/Vy;
 B2=E2/Vy;
 t=0.;
_pause();
}

double ax,az,cs,sc;
public void setpc (double p[][][],double c) {
 cs=Math.cos(c);
 sc=Math.sin(c);
 for(int i=0;i<nx;i++){
  for(int j=0;j<nn;j++){
   ax=xmin+dd2*i;
   az=zmin+dd*j;
   p[i][j][0]=ax*cs-az*sc;
   p[i][j][2]=ax*sc+az*cs;
  }
 }
}
public void setpy (double p[][][],double yp) {
 for(int i=0;i<nx;i++){
 for(int j=0;j<nn;j++){
   p[i][j][1]=yp;
 }
 }

}
