public double calE (double ex,double ez) {
 return ((int)((ex*ex+ez*ez)*0.1+0.5))/.1;
}
double cs1,cs2;
public void calEB(int di,double cta,double Ez[],double Ex[],double Bz[],double Bx[]){
cs1=Math.cos(cta);
cs2=Math.cos(cta+phase);
EZ[0]=E1*cs1;
EX[0]=E2*cs2;
BZ[0]=-EX[0]/Vy;//-B2*cs2;
BX[0]=EZ[0]/Vy;//B1*cs1;


for(int i=1;i<np1;i++){
 E=EZ[i-1]*Math.sin(c[i])+EX[i-1]*Math.cos(c[i]);
 EX[i]=E*Math.cos(c[i]);//*Math.cos(c[i])+E*Math.sin(c[i-1])*Math.sin(c[i]);
 EZ[i]=E*Math.sin(c[i]);//*Math.sin(c[i])+E*Math.cos(c[i-1])*Math.cos(c[i]);
 BZ[i]=-EX[i]/Vy; //BZ[i-1]*Math.sin(dc);
 BX[i]=EZ[i]/Vy; //BX[i-1]*Math.cos(dc);
}
 Ez[di]=EZ[fid];//E1
 Bx[di]=BX[fid];//B1 
 Ex[di]=EX[fid];//E2
 Bz[di]=BZ[fid];// -B2
}

public void playpause () {
 if(label==l_play){
  label=l_pause;
  _play();
 }else{
  label=l_play;
  _pause();
 }
}

