public boolean findnext(int j,double c){ // global m, id2,xt,yt,cp
//System.out.println("j="+j+",I="+I[j]+",c="+c+",x="+cx[j]+",y="+cy[j]);
  np=0;
  m=Math.tan(c);
  cs1=Math.cos(c);
  sc1=Math.sin(c);
  clr[j]=getColor(I[j]);
  b=cy[j]-m*cx[j];
  id=idr[j];
 for(int i=0;i<n;i++){
  if(i==id)continue; // itself
  m1=Math.tan(cs[i]);
  if(Math.abs(m)>max){// vertical line
    if(Math.abs(m1)>max)continue;// also vertical , parallel , no contact 
    xt=cx[j];
    yt=m1*(xt-px[i])+py[i];
  }else if(px[i+1]==px[i]){// vertically line
    xt=px[i];
    yt=m*xt+b;
  }else {
    if(Math.abs(m-m1)<min)continue;// parallel 
    b1=py[i]-m1*px[i];
    xt=(b-b1)/(m1-m);
    yt=m*xt+b;
  }
  if(((xt-cx[j])*cs1+(yt-cy[j])*sc1>0)&& (xt-px[i])*(xt-px[i+1])+(yt-py[i])*(yt-py[i+1])<0){
   x[np]=xt;
   y[np]=yt;
   ids[np]=i;
   np++;
  }
 }
if(np==0){// can not find contact point, draw ray
 if(Math.abs(Math.cos(c))>Math.abs(Math.sin(c))){
   if(Math.cos(c)>0)dx[j]=2*xmax-cx[j];
   else dx[j]=2*xmin-cx[j];
   dy[j]=dx[j]*m;//Math.tan(c);
 }else{
   if(Math.sin(c)>0)dy[j]=2*ymax-cy[j];
   else dy[j]=2*ymin-cy[j];
   dx[j]=dy[j]/m;//Math.tan(c);
 }
 return false;
}
id2=-1;
r=rmax;
 for(int i=0;i<np;i++){// find shortest distance
  r1=(cx[j]-x[i])*(cx[j]-x[i])+(cy[j]-y[i])*(cy[j]-y[i]); 
  if(r1<r){
    id2=ids[i];
    xt=x[i];
    yt=y[i];
    cp=cs[id2];
    r=r1;
  }//if
 }//for
 dx[j]=xt-cx[j];
 dy[j]=yt-cy[j];
 return true;
}

public java.awt.Color getColor (double v) {
  if(showall)return new java.awt.Color(0,0,255);
  iv=(int)(v*255);
  if(iv<0){//should not happened
    iv=0;
  }else if(iv>255){
    iv=255;
  }
  return new java.awt.Color(0,0,iv,iv);
}

public double getreflections(double c1,double c2){
 if(Math.abs(Math.sin(c1+c2))<min)dv1=(index-1)/(index+1);
 else dv1=Math.sin(c1-c2)/Math.sin(c1+c2);
 return dv1*dv1;
}
public double getreflectionp(double c1,double c2){
 if(Math.abs(Math.sin(c1+c2))<min)dv1=(index-1)/(index+1);
 else dv=Math.tan(c1-c2)/Math.tan(c1+c2);
 return dv1*dv1;
}

public void setdydx(int i,double c){
 if(Math.abs(Math.sin(c))<min){
  if(Math.cos(c)>0)dx[i]=2*xmax-dx[i];
  else dx[i]=2*xmin-dx[i];
  dy[i]=dx[i]*Math.tan(c);
 }else{
  if(Math.sin(c)>0)dy[i]=ytop-cy[i];
  else dy[i]=-ytop-cy[i];
  dx[i]=dy[i]/Math.tan(c);
 }
}

public void dragit(){
 if(checkit)for(int i=0;i<n;i++){
   checkdrag(i);
 }
  if(dragid==n-1)id1=0;
  else id1=dragid+1;
  if(dragid==0)id=n-1;
  else id=dragid-1;
  m=(px[id]-px[dragid])*(px[id1]-px[dragid])+(py[id]-py[dragid])*(py[id1]-py[dragid]);
  m=m/Math.sqrt(((px[id]-px[dragid]))*((px[id]-px[dragid]))+(py[id]-py[dragid])*(py[id]-py[dragid]))/Math.sqrt((px[id1]-px[dragid])*(px[id1]-px[dragid])+(py[id1]-py[dragid])*(py[id1]-py[dragid]));
  msg="angle="+((int)(Math.acos(m)*c2a*10+0.5)/10.);
  setit();
}
public void checkdrag (int dragid) {
  if(dragid==n-1)id1=0;
  else id1=dragid+1;
  if(dragid==0)id=n-1;
  else id=dragid-1;
  c=Math.atan2(py[id1]-py[dragid],px[id1]-px[dragid]);
  c1=Math.atan2(py[id1]-py[id],px[id1]-px[id]);
  if(c>c1 &&c-c1<pi){
    System.out.println("c="+c+",c1="+c1);
    r=(py[id1]-py[id])*(py[id1]-py[id])+(px[id1]-px[id])*(px[id1]-px[id]);
    m=((py[id1]-py[dragid])*(py[id1]-py[id])+(px[id1]-px[dragid])*(px[id1]-px[id]))/r;
    xt=m*(px[id1]-px[id]);//b
    yt=m*(py[id1]-py[id]);
    px[dragid]-=2*(xt-(px[id1]-px[dragid]));// 2(a-b)
    py[dragid]-=2*(yt-(py[id1]-py[dragid]));
  }
  if(dragid==n-1)for(int i=n;i<n0;i++){
    px[i]=px[dragid];
    py[i]=py[dragid];
  }
}

