public void setit () {
  for(int i=n;i<=n0;i++){
   px[i]=px[0];
   py[i]=py[0];
   pxy[i][0]=px[i];
   pxy[i][1]=py[i];
 }
 for(int i=0;i<n;i++){
  cs[i]=Math.atan2(py[i+1]-py[i],px[i+1]-px[i]);
   pxy[i][0]=px[i];
   pxy[i][1]=py[i];
 }
 sizep=size/Math.sqrt(n);
 checkit=false;
 nmode=false;
 _step(); 
}

public void setpolygons () {
  for(int i=0;i<n;i++){
    px[i]=pxy[i][0];
    py[i]=pxy[i][1];
  }
  pxy[n][0]=px[0];
  pxy[n][1]=py[1];
  setit();
}

