double cta,r1,r2;
public void setprism(){
  dragprism();
  cta=(prismc)*pi/180.;
  R=r1=Math.sqrt((px[1]-px[2])*(px[1]-px[2])+(py[1]-py[2])*(py[1]-py[2]));
  px[1]=px[2]+r1*Math.cos(ci[2]-cta);
  py[1]=py[2]+r1*Math.sin(ci[2]-cta);
//  dragprism();
}
public void dragprism () {
 area=calarea(px[1]-px[0],py[1]-py[0], px[2]-px[0],py[2]-py[0]);
 for(int i=0;i<n;i++){
  ci[i]=Math.atan2(py[i+1]-py[i],px[i+1]-px[i]);
 }
  // take care of cross over
  r1=Math.sqrt((px[1]-px[2])*(px[1]-px[2])+(py[1]-py[2])*(py[1]-py[2]));
  r2=Math.sqrt((px[3]-px[2])*(px[3]-px[2])+(py[3]-py[2])*(py[3]-py[2]));
  prismc=Math.acos(((px[1]-px[2])*(px[3]-px[2])+(py[1]-py[2])*(py[3]-py[2]))/(r1*r2))*180./pi;
  if(prismc>175){
   prismc=174;
   setprism();
  }else if(prismc<5){
   prismc=6;
   setprism();
  }
 cn=ci[0]-pi/2;
// csn=Math.cos(cn);
// scn=Math.sin(cn);
 Ln=Math.sqrt((px[1]-px[0])*(px[1]-px[0])+(py[1]-py[0])*(py[1]-py[0]));
 if(R<Ln/1.8)R=Ln/1.8;
 setN();
}

public void setN(){
 dn=Math.sqrt(R*R-Ln*Ln/4);
 xn=(px[0]+px[1])/2+dn*Math.cos(cn);
 yn=(py[0]+py[1])/2+dn*Math.sin(cn);
 double dc;
 cin=Math.atan2(py[0]-yn,px[0]-xn);
 cfn=Math.atan2(py[1]-yn,px[1]-xn);
 if(Math.abs(cfn-cin)>pi)cfn=cfn-pi*2;
 dcn=(cfn-cin)/(nn-2);
 pxn[0]=px[2]; pyn[0]=py[2];
 for(int i=0;i<nn;i++){
  c=cin+i*dcn;
  pxn[i+1]=xn+R*Math.cos(c);
  pyn[i+1]=yn+R*Math.sin(c);
 }
 pxn[nn+1]=px[0]; pyn[nn+1]=py[0];
}
public double calarea(double ax,double ay,double bx,double by){
 return Math.abs((ax*by-ay*bx))/2.;
}

public boolean outside(double x,double y){// check if point (x,y) outside prism
 a=0;
 for(int i=0;i<n;i++)  a+=calarea(px[i+1]-x,py[i+1]-y,px[i]-x,py[i]-y);
 return a>1.01*area;
}

double c0,m0,rmin,rt,m;
int index;
public int calc_contactpoint(){
 m=Math.tan(c);
 rmin=range;
 index=-1;
 for(int i=0;i<n;i++){
   if(i==id)continue;
   c0=Math.atan2(py[i+1]-py[i],px[i+1]-px[i]);
   m0=Math.tan(c0);
   xt[i]=((py[i]-m0*px[i])-(y-m*x))/(m-m0);
   yt[i]=m0*(xt[i]-px[i])+py[i];
   rt=Math.sqrt((xt[i]-x)*(xt[i]-x)+(yt[i]-y)*(yt[i]-y));
   if( (!outside(xt[i],yt[i])) && ((xt[i]-x)*cs+(yt[i]-y)*sc>0) && (rt<rmin) ){
      // in the prism && forward direction && less distance
     rmin=rt;
     index=i;
   }
 }
 if( index==0 && lenmode){//npt==0 &&
   cnc=pi+Math.atan(m)-Math.asin( ((y-m*x)-(yn-m*xn))*Math.abs(Math.cos(c))/R);
   xnc=xn+R*Math.cos(cnc);
   ync=yn+R*Math.sin(cnc);
 }
 return index;// if(index>-1)// find contact point else did not hit prism
}

public double calcR (double c1,double c2,double n21) {
 double rn,rp,r,cs1,cs2;
 cs1=Math.abs(Math.cos(c1));
 cs2=Math.abs(Math.cos(c2));
 rn=(cs1-n21*cs2)/(cs1+n21*cs2); // sin(c1-c2)/sin(c1+c2)
 rp=(cs2-n21*cs1)/(cs2+n21*cs1);// tan(c1-c2)/tan(c1+c2)
 if(pmode)return rp*rp;
 else if(nmode)return rn*rn;
 return r=(rn*rn+rp*rp)/2.;
}

double dv;
public double dvalue (double v) {
 if(v>0)dv=0.5;
 else if(v<0)dv=-0.5;
 else dv=0.;
 return (int)(v*10+dv)/10.;
}

