if(showC){
 if(Math.abs(Vt)>VC){// external voltage > vapacitor valtage =>charing
  IC=(Math.abs(Vt)-VC)/r;
  IR=VC/R;
  I=IC+IR;
 }else{// discharge 
  IR=VC/R;
  IC=-IR;
  I=0;
 }
 if(I>0){//charging
  for(int i=0;i<nrc1;i++){
   if(cflow){
    xr[i]-=I/scale;
    if(xr[i]>0 && xr[i]<D/4)xr[i]=D2-(D/4-xr[i]);
    else if(xr[i]<0 && xr[i]<-D2)xr[i]=-D/4-(-D2-xr[i]);
   }else if(eflow){
    xr[i]+=I/scale;
    if(xr[i]<0 && xr[i]>-D/4)xr[i]=-D2+(xr[i]+D/4);
    else if(xr[i]>0 && xr[i]>D2)xr[i]=D/4+(xr[i]-D2);
   }
  }// for
 }//end I>0 
 if(cflow)sign=-1;
 else if(eflow)sign=1;
 else sign=0.;
 for(int i=0;i<nrc2h;i++){
  xh1[i]+=sign*IR/scale;
  if(xh1[i]<-D/4)xh1[i]=D/4-(-D/4-xh1[i]);
  else if(xh1[i]>D/4)xh1[i]=-D/4+xh1[i]-D/4;
  xh2[i]+=sign*IC/scale;
  if(xh2[i]<-D/4)xh2[i]=D/4-(-D/4-xh2[i]);
  else if(xh2[i]>D/4)xh2[i]=-D/4+xh2[i]-D/4;
 }
 for(int i=0;i<nrc2v;i++){
  yv1[i]+=sign*IC/scale;
  yv2[i]-=sign*IC/scale;
  if(yv1[i]>0)yv1[i]=-size*3+yv1[i];
  else if(yv1[i]<-size*3)yv1[i]=yv1[i]+size*3;
  if(yv2[i]>0)yv2[i]=-size*3+yv2[i];
