for(int i=0;i<n2;i++){
 x21[i]+=di;
 if(x21[i]>0)x21[i]=x0+x21[i];
 else if(x21[i]<x0)x21[i]=x21[i]-x0;
 x22[i]-=di;
 if(x22[i]>0)x22[i]=x0+x22[i];
 else if(x22[i]<x0)x22[i]=x22[i]-x0;
}
for(int i=0;i<n;i++){
