Content.Code:
x = 1.0;
y = x+L;
vx = vy = -1.;
t = x/vx;
ncol = 0;
computed = false;

EventContent.Action:
vx = Math.abs(vx);
ncol++;
_resetSolvers();
if (stopcol) _pause();
computed = true;

Content.Code:
if (alpha < alphaMin)
  alpha = alphaMin;
b = alpha*a;  
massX = x+.5*a;
massY = y+a+.5*b;
partX = (x+alpha*y)/(1.+alpha)+.5*(a+b);
boxX = .5*a+.5*b+.5*(x+y);
boxSize = a+b+y-x;
