Content.Code:
t = t1 = t2 = state[2] = state[3] = 0.0;
if (phi0 < 0.0) phi0 = 0.0;
else if (phi0 >= 89.9) phi0 = 89.9;
z0 = Math.sin(state[0] = state[1] = Math.toRadians(phi0));
_view.resetTraces();

public double[] derivatives(double t,double state[]) {
    double [] vel = new double[4];
    vel[0] = state[2];
    vel[1] = state[3];
    vel[2] = -1.5*Math.cos(state[0]);
    double cs = Math.cos(state[1]);
    vel[3] = 3.0*cs*(state[3]*state[3]*Math.sin(state[1])-2.0)/(1.0+3.0*cs*cs);
    return vel;
}
