//computes x-component of projectile's acceleration
function getAx (x,y) {

    return -Guniversal*M*x/Math.pow(x*x+y*y,1.5);
}

//computes y-component of projectile's acceleration
function getAy (x,y) {

    return -Guniversal*M*y/Math.pow(x*x+y*y,1.5);
}
