double dv=0.;

// for string value of l_answer and l_x
public String dvalue (double v,double unit) {
 if(v>0)dv=0.5;
 else if(v<0)dv=-0.5;
 else dv=0.;
 if(unit==0.1)out=(int)(v*10.+dv)/10.;
 else if(unit==0.02)out=((int)(v*50.+dv))/50.; // 50. instead of 50 fix bug of pointer
 //else if(unit==0.05)out=((int)(v*100.+dv)/5)/20.;
 else if(unit==0.05)out=((int)(v*20.+dv))/20.; // by hwang to fix bug when 0.08 being read as 0.05 instead of 0.1  if the marking are 0, 0.05 0.1.
 if(unit!=0.1 && out*10-(int)(out*10)==0)return out+"0"; //lookang 0 to address 14.00 instead of14.0
 return out+"";
}

double ddv=0.;

// for double value of answer
public Double ddvalue (double v,double unit) {
 if(v>0)ddv=0.5;
 else if(v<0)ddv=-0.5;
 else ddv=0.;
 if(unit==0.1)out=(int)(v*10.+ddv)/10.;
 else if(unit==0.02)out=((int)(v*50+ddv))/50.;
 //else if(unit==0.05)out=((int)(v*100.+dv)/5)/20.;
 else if(unit==0.05)out=((int)(v*20.+ddv))/20.; // by hwang to fix bug when 0.08 being read as 0.05 instead of 0.1  if the marking are 0, 0.05 0.1.
 if(unit!=0.1 && out*10-(int)(out*10)==0)return out*1;
 return out;
}

double dv1;
public String double2String(double v,double divider){
 if(v>0)dv1=0.5;
 else if(v<0)dv1=-0.5;
 else dv1=0;
 return (int)(v*divider+dv1)/divider+"";
}

// this code has to do with displaying the number 2.3 instead of 2.300000001 when using % %

Osejs.View.Creation.Element.Property:
//mxs=mx; // store mouse x position
if (x >= xmax-x2-1) x= xmax-x2-1;
y = 0; // test for forcing no anyhow moving

//if(x<0)x=0; // for the 2 jaws to meet at zero
oxmax=oxmin+x;
if(w>x){ // if width of blue object larger than jaws gap
  if(oy>oymin-h/2)oy=oymin-h/2; // if blue object try to move in the jaws, block it with oy=oymin-h/2
  outobject=true; // outside object is true 
}else{// object is smaller than the width
 if(outobject){ // if outisde object is true
   if(ox-w/2>oxmin && ox+w/2<oxmax && oy+h/2>oymin){// move inside
     outobject=false; // blue object is inside jaws
   }else{
     if(ox-w/2<oxmin || ox+w/2>oxmax){ // if blue object is force outwards
       if(oy+h/2>oymin)oy=oymin-h/2; // if also inside jaws, then block with oy=oymin-h/2
     }
   }
 }else{
  if(oy+h/2<oymin)outobject=true;// move outside again
  else{
   if(oy+h/2>oymax)oy=oymax-h/2; // top limit reach, block
   if(ox-w/2<oxmin)ox=oxmin+w/2; // left limit reach,block
   else if(ox+w/2>oxmax)ox=oxmax-w/2; //right limit reach, block
  }
 }  
}


