double dT=0.,I,r,dr;
public void changeRange () {

 dT=(T2-T1)/(n-1);
 for(int i=0;i<n;i++){
   TS[i]=T2-i*dT;
//  xm=TX[i]=h*c/(4.97*k*TS[i])*1.e10;
// code to appear Tmax top position
// taken from http://en.wikipedia.org/wiki/Planck%27s_law
// This function peaks for hc = 4.97?kT, a factor of 1.76 shorter in wavelength (higher in frequency) than the frequency peak. It is the more commonly used peak in Wien's displacement law.
// xm is ? where peak occurs
 xm=TX[i]=h*c/(4.97*k*TS[i])*1.e9; // cos weelk doing nm instead of Am
 xmred=TXred=h*c/(4.97*k*T)*1.e9;
//  TMSG[i]="T="+((int)(TS[i]*10+0.5)/10.)+"K, peak at "+(int)(xm*10+0.5)/10. +"A";
 TMSG[i]="T="+((int)((TS[i]*10+0.5)/10.))+"K, peak at "+(int)((xm*10+0.5)/10.) +" nm";
 TMSGred="T="+((int)((T*10+0.5)/10.))+"K, peak at "+(int)((xmred*10+0.5)/10.) +" nm";

//   TY[i]=twohc2/((Math.exp(cst/(xm*TS[i]))-1)*(xm*xm*xm*xm*xm));
// calculate position of text top
 TY[i]=twohc2/((Math.exp(cst/(xm*TS[i]))-1)*(xm*xm*xm*xm*xm));
 TYred=twohc2/((Math.exp(cst/(xmred*T))-1)*(xmred*xmred*xmred*xmred*xmred));

}
 if(T<T1)T=T1;
 if(T>T2)T=T2;
Imax=0;
dr=(rmax-rmin)/(np-1);
for(int i=0;i<np;i++){
  r=rmin+i*dr;
   I=twohc2/((Math.exp(cst/(r*T2))-1)*(r*r*r*r*r));
 if(I>Imax)Imax=I;
   I=twohc2/((Math.exp(cst/(r*T1))-1)*(r*r*r*r*r));
// if(I>Imax)Imax=I;
//   I=twohc2/((Math.exp(cst/(r*T))-1)*(r*r*r*r*r));
 if(I>Imax)Imax=I;
}
Imax=Imax*1.05;
}


