public int funcion( double t , double origen, double factor) {
double resultado;

resultado = 128.0+128.0*(Math.exp( factor*(t-origen) )-Math.exp(-factor*(t-origen)))/(Math.exp(factor*(t-origen))+Math.exp(-factor*(t-origen)));

return( (int)resultado );
}
