double dv;
public String double2String(double v,double divider){
 if(v>0)dv=0.5;
 else if(v<0)dv=-0.5;
 else dv=0;
 return (int)(v*divider+dv)/divider+"";
}

// this code has to do with displaying the number 2.3 instead of 2.300000001 when using % %

double ddv;
public Double ddouble2String(double v,double divider){
 if(v>0)ddv=0.5;
 else if(v<0)ddv=-0.5;
 else ddv=0;
 return (int)(v*divider+ddv)/divider;
}

// this code has to do with displaying the number 2.3 instead of 2.300000001 when using % %

Thread upThread;

public void startRachet () {
  if(lock){
    _view.alert("micrometerFrame","Locked position.","The micrometer position is locked.");
    return;
  }
  Runnable ratchet = new Runnable() {
    public synchronized void run() {
      while(upThread!=null) {
        _model._step();
        step*=1.1; // slowly increase micrometer step to speed rotation
        try {
          Thread.sleep(100);
        } catch(InterruptedException ie) {
        }
      }
    }
  } ;
  upThread=new Thread(ratchet);
  upThread.start();
}

public void stopRachet () {
  upThread=null;
  step=step0;
}

