//add x-zero offset later
public double spline_evaluatory(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=spline_ay[is]+(spline_by[is]+(spline_cy[is]+spline_dy[is]*zz)*zz)*zz;
  return val;
}
//evaluate y spline parameters from knots
public void spline_getdatay() {
  spline_N=N; //no bounds checking here...
  for(int i=0;i<=spline_N;i++) {
    spline_fy[i]=interface_y[i];
  }

  spline_chiy[0]=spline_beta*(spline_fy[1]-spline_fy[0]);
  spline_phiy[0]=spline_chiy[0];
  for(int i=1;i<=spline_N-1;i++) {
    spline_chiy[i]=3.0*(spline_fy[i+1]-spline_fy[i-1]);
    spline_phiy[i]=spline_gamma[i]*(spline_chiy[i]-spline_phiy[i-1]);
  }
  spline_chiy[spline_N]=spline_beta*(spline_fy[spline_N]-spline_fy[spline_N-1]);
  spline_phiy[spline_N]=spline_gamma[spline_N]*(spline_chiy[spline_N]-spline_alpha*spline_phiy[spline_N-1]);

  spline_Dy[spline_N]=spline_phiy[spline_N];
  for(int i=1;i<=spline_N;i++) {
    spline_Dy[spline_N-i]=spline_phiy[spline_N-i]-spline_gamma[spline_N-i]*spline_Dy[spline_N-i+1];
  }

  for(int i=0;i<=spline_N-1;i++) {
    spline_ay[i]=spline_fy[i];
    spline_by[i]=spline_Dy[i];
    spline_cy[i]=3.0*(spline_fy[i+1]-spline_fy[i])-spline_Dy[i+1]-2.0*spline_Dy[i];
    spline_dy[i]=spline_Dy[i+1]+spline_Dy[i]-2.0*(spline_fy[i+1]-spline_fy[i]);


  }
}
//initialize spline constants for this number of knots and type of spline
public void spline_gamma_init () {
  if(spline_is_natural) {
    spline_alpha=.5;
    spline_beta=1.5;
  } else {
    spline_alpha=1;
    spline_beta=2;
  }
  spline_gamma[0]=spline_alpha;
  for(int i=1;i<=spline_N-1;i++) {
    spline_gamma[i]=1.0/(4.0-spline_gamma[i-1]);
  }
  spline_gamma[spline_N]=1.0/(1.0-spline_alpha*spline_gamma[spline_N-1]);
}

//add x-zero offset later
public double spline_evaluatorx(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=spline_ax[is]+(spline_bx[is]+(spline_cx[is]+spline_dx[is]*zz)*zz)*zz;
  return val;
}
//evaluate x spline parameters from knots
public void spline_getdatax() {
  spline_N=N; //no bounds checking here...
  for(int i=0;i<=spline_N;i++) {
    spline_fx[i]=interface_x[i];
  }

  spline_chix[0]=spline_beta*(spline_fx[1]-spline_fx[0]);
  spline_phix[0]=spline_chix[0];
  for(int i=1;i<=spline_N-1;i++) {
    spline_chix[i]=3.0*(spline_fx[i+1]-spline_fx[i-1]);
    spline_phix[i]=spline_gamma[i]*(spline_chix[i]-spline_phix[i-1]);
  }
  spline_chix[spline_N]=spline_beta*(spline_fx[spline_N]-spline_fx[spline_N-1]);
  spline_phix[spline_N]=spline_gamma[spline_N]*(spline_chix[spline_N]-spline_alpha*spline_phix[spline_N-1]);

  spline_Dx[spline_N]=spline_phix[spline_N];
  for(int i=1;i<=spline_N;i++) {
    spline_Dx[spline_N-i]=spline_phix[spline_N-i]-spline_gamma[spline_N-i]*spline_Dx[spline_N-i+1];
  }

  for(int i=0;i<=spline_N-1;i++) {
    spline_ax[i]=spline_fx[i];
    spline_bx[i]=spline_Dx[i];
    spline_cx[i]=3.0*(spline_fx[i+1]-spline_fx[i])-spline_Dx[i+1]-2.0*spline_Dx[i];
    spline_dx[i]=spline_Dx[i+1]+spline_Dx[i]-2.0*(spline_fx[i+1]-spline_fx[i]);


  }
}

public void plotter () {
  for(int i=0;i<pn;i++) {
    ps=pds0*(float)i;
    px[i]=spline_evaluatorx(ps);
    pdx[i]=spline_evaluatorx(ps+pds0)-px[i];

    py[i]=spline_evaluatory(ps);
    pdy[i]=spline_evaluatory(ps+pds0)-py[i];
  }
}

public void controls_limits () {
  N=Math.min(N_max,Math.max(0,N));
  for(int i=0;i<=N_max;i++) {
    interface_x[i]=Math.min(20.0,Math.max(interface_x[i],0.0));
    interface_y[i]=Math.min(20.0,Math.max(interface_y[i],0.0));
  }
}
public void update_spline() {
  //stuff that needs to be called when spline is changed
  //set the spline
  controls_limits ();
  spline_getdatay();
  spline_getdatax();
  //update the stuff for dynamics including stuff for display
  KE=.5*mass*coaster_v0*coaster_v0; // KE = 0.5*m*v*v added by weelk

E=mass*g*ys(0.0)+KE; // TE = mgh + KE added by weelk
  double xp=xsp(0.);//only evaluate these once...
  double yp=ysp(0.);
  coaster_sv0=coaster_v0/Math.sqrt(xp*xp+yp*yp);

  coaster_reset();  //track/launch speed change ->reset cart to start

}

public void play_it() {
  //update_spline();
  _play();
}
public void pause_it() {
  _pause();
}

//this function evaluates x as a function of s
public double xs(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=spline_ax[is]+(spline_bx[is]+(spline_cx[is]+spline_dx[is]*zz)*zz)*zz;
  return val;
}
//this function evaluates the the first derivative of x wrt s
public double xsp(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=spline_bx[is]+(2.0*spline_cx[is]+3.0*spline_dx[is]*zz)*zz;
  return val/spline_delta;
}
//this function evaluates the the second derivative of x wrt s
public double xspp(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=2.0*spline_cx[is]+6.0*spline_dx[is]*zz;
  return val/(spline_delta*spline_delta);
}
//this function evaluates y as a function of s
public double ys(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=spline_ay[is]+(spline_by[is]+(spline_cy[is]+spline_dy[is]*zz)*zz)*zz;
  return val;
}
//this function evaluates the the first derivative of y wrt s

public double ysp(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=spline_by[is]+(2.0*spline_cy[is]+3.0*spline_dy[is]*zz)*zz;
  return val/spline_delta;
}
//this function evaluates the the second derivative of y wrt s
public double yspp(double this_s) {
  double scaled_s=this_s/spline_delta;
  int is=(int)(scaled_s);
  is = Math.min(Math.max(is,0),spline_N-1);
  double zz=scaled_s-(float)is;
  double val=2.0*spline_cy[is]+6.0*spline_dy[is]*zz;
  return val/(spline_delta*spline_delta);
}
//this function evaluates the the second derivative of s wrt time
public double sdd(double s, double sd) {
  double xp=xsp(s);//only evaluate these once...
  double yp=ysp(s);
// from description dynamics dcoaster_sv/dt = -g*yp ....
double val=-g*yp-1.0*(xp*xspp(s)+yp*yspp(s))*sd*sd;
//  double val=-mass*g*yp-1.0*(xp*xspp(s)+yp*yspp(s))*sd*sd; // added mass
  val=val/(xp*xp+yp*yp);
  return val;
}

