public void service_opts() {
  showN=false;
  if (which_opt.startsWith("ramp")) set_ramp();
  else if (which_opt.startsWith("up")) set_up_down_up();
  else if (which_opt.startsWith("loop")) set_loop1();
  else if (which_opt.startsWith("circularloop")) set_loopcircular(); // added by lookang for circular loop
  else if (which_opt.startsWith("2loops")) set_loop2();
  else if (which_opt.startsWith("figure 8")) set_fig8();
  else if (which_opt.startsWith("custom")) set_custom(10);
  else set_ramp();//if all else fails
}

public void set_custom(int N) {
  showN=true;
  this.N=N;
  delta=1.0/(float)N;
  spline_delta=delta;
  spline_N=N;
  for(int i=0;i<=N_max;i++) {
    interface_x[i]=(float)i/(float)N*20.;
    interface_y[i]=(float)(N-i)/(float)N*20.;
  }
  spline_gamma_init ();
  update_spline();
  pds0=(float)(N)*delta/(float)pn;
  plotter ();
which_opts = "custom"; // added in attempt in make it remember by weelk
}

public void set_ramp() {
  N=7;
  delta=1.0/(float)N;
  spline_delta=delta;
  spline_N=N;
  for(int i=0;i<=N_max;i++) {
    interface_x[i]=(float)i/(float)N*20.;
    interface_y[i]=(float)(N-i)/(float)N*20.;
  }
  spline_gamma_init ();
  update_spline();

  pds0=(float)(N)*delta/(float)pn;
  plotter ();
which_opts = "ramp"; // added in attempt in make it remember by weelk
}


