public void coaster_set () {
  coaster_x=xs(coaster_s);
  coaster_y=ys(coaster_s);
  coaster_vx=xsp(coaster_s)*coaster_sv;
  coaster_vy=ysp(coaster_s)*coaster_sv;
  coaster_v=Math.sqrt(coaster_vx*coaster_vx+coaster_vy*coaster_vy);
  v_display = String.format("V = %4.1f", coaster_v);
  coaster_theta=Math.atan2(ysp(coaster_s),xsp(coaster_s));
  coaster_ax=xspp(coaster_s)*coaster_sv*coaster_sv+xsp(coaster_s)*coaster_sa;
  coaster_ay=yspp(coaster_s)*coaster_sv*coaster_sv+ysp(coaster_s)*coaster_sa;
  Rx=mass*coaster_ax; // Reaction forcex = mass*coaster_ax
  Ry=mass*(coaster_ay+g); // added mass by weelk
  //fix reaction force -take only components perp to track
  vhat_x=xsp(coaster_s);//tangent unit vector
  vhat_y=ysp(coaster_s);
  vn=Math.sqrt(vhat_x*vhat_x+vhat_y*vhat_y);
  vhat_x=vhat_x/vn;
  vhat_y=vhat_y/vn;
  nhat_x=-vhat_y;// track norml = tangent rotated 90 degrees
  nhat_y=vhat_x;
  rdn=Rx*nhat_x+Ry*nhat_y;
  Rx=rdn*nhat_x;
  Ry=rdn*nhat_y;
  //set spring
  spring_x=xs(.999);
  spring_y=ys(.999);
  spring_theta=Math.atan2(ysp(.999),xsp(.999));
 //set launcher
  launcher_x=xs(.0);
  launcher_y=ys(.0);
  launcher_theta=Math.atan2(ysp(.0),xsp(.0))+3.14159;

}

public void coaster_reset() {
  coaster_s=0;
  coaster_sv=coaster_sv0;
  t=0;
}

Osejs.View.Creation.Element.Property:
N=Math.max(2,N);
N=Math.min(N,spline_Nmax);  // limit the maximum
set_custom(N);
