t += dt;
phi += w*dt;
_view.setValue("phi",phi);  // informs view that phi has changed

// transform space frame omega to body frame
omega=_view.block.toBodyFrame(new double[]{0,0,w});
// compute angular momentum in body frame
LBody[0]=I1*omega[0];
LBody[1]=I2*omega[1];
LBody[2]=I3*omega[2];

// start with diagonal inertia tensor in body frame
I[0][0]=I1;
I[0][1]=0;
I[0][2]=0;
I[1][0]=0;
I[1][1]=I2;
I[1][2]=0;
I[2][0]=0;
I[2][1]=0;
I[2][2]=I3;
// compute transformation from body frame to space frame
Matrix3DTransformation transform=Matrix3DTransformation.rotationZ(phi);  // rotation about axle
transform.multiply(Matrix3DTransformation.rotationX(tilt));              // tilt of box on axle
//transform inertia tensor into body frame
I=transform.direct(I);
// compute angular momentum in space frame
LxSpace=I[0][2]*w;
LySpace=I[1][2]*w;
LzSpace=I[2][2]*w;
// compute torque in space frame using cross product of omega and L
N1Space=-w*LySpace*5;
N2Space=w*LxSpace*5;
N3Space=0;
